import {LOGIN} from '../constan';
import {LOGIN_ERROR} from '../constan';
import axios from "axios";

export const LoginAction = (payload) => (dispatch, getState)=>{
    return new Promise((resolve, reject) =>{
 
        const baseUri = "https://private-117dfb-logeo.apiary-mock.com/usuarios";

        const data = {
            usuario: "user",
            contrasena: "pass",
          };
          axios
            .post(baseUri, data)
            .then((res) => {
              return res.data;
            })
            .then((res) => {
              if (res.esActivo === true) {
               // cookies.set("usuario", res.usuario, { path: "/" });
                window.location.href = "./home";
              } else {
                alert("Las credenciales son invalidas. Revisa con el administrador.");
              }
            })
            .catch((err) => {
              alert("Ocurrio un error de conexión: " + err);
            });
});
}
